    @State private var currentAnimal: Animal? = nil

    var body: some View {
        VStack(spacing: 24) {
            Text("Find an Animal")
                .font(.largeTitle.bold())
                .padding(.top, 32)
            Spacer()
            if let currentAnimal {
                VStack(spacing: 16) {
                    Image(currentAnimal.imageName)
                        .resizable()
                        .scaledToFit()
                        .frame(height: 240)
                        .clipShape(RoundedRectangle(cornerRadius: 24, style: .continuous))
                        .shadow(radius: 8)
                        .accessibilityHidden(true)
                    Spacer()
                    Text("\(currentAnimal.name)")
                        .font(.title)
                        .multilineTextAlignment(.center)
                        .padding(.horizontal)
                }
            } else {
                Text("Tap the button to get an animal to find!")
                    .font(.title2)
                    .multilineTextAlignment(.center)
                    .padding(.horizontal)
                    .accessibilityLabel("No animal selected yet")
            }
            
            Button(action: assignRandomAnimal) {
                Label("Give me an animal", systemImage: "pawprint.fill")
                    .font(.title3.weight(.semibold))
                    .padding()
                    .frame(maxWidth: .infinity)
            }
            .buttonStyle(.borderedProminent)
            .padding(.horizontal)
            .accessibilityHint("Provides a random animal for the player to find on the map.")

        }
    }

    private func assignRandomAnimal() {
        currentAnimal = animals.randomElement()
    }
}

